import React, { useEffect, useState } from 'react';
import Header from "./HeaderComponent";
import Footer from "./FooterComponent";
import { useParams } from 'react-router-dom';

import { Helmet } from 'react-helmet';
import CarCategoriesCard from '../../CarCategoriesCard';

function BrandDetailCarComponent(){


    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { brand } = useParams(); // Access the route parameter


    useEffect(() => {
      fetchData(brand);
    }, []);

    const fetchData = async (brand) => {
      try {
        const response = await fetch('https://app2.movlife.co/jsongoogle.php?promocion=yes'); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error('Network response was not ok.');
        }
        const data = await response.json();
        console.log(data);
        setData(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    const carBrands = data; // Get the car details based on the route parameter

    if (!carBrands) {
      return <p>Car not found</p>;
    }

    const carCategoriesComponents = [];
    carBrands.forEach((carDetails) => {
        carCategoriesComponents.push(
            <CarCategoriesCard {...carDetails} ></CarCategoriesCard>
        );
      });

    return(
        <div>
            <Helmet>
                <title>Emi Ride Rent a Car Dubai - Promotions</title>
                <meta name="description" content="About our renting company, clarify your doubts about renting a car to ride on dubai" />
            </Helmet>

            <Header/>
            <div className="about-us-container">
            <div className='best-choice-container d-flex  align-items-center'>


{/* <img className='best-choice-header' src={BestChoiceImg} width="5650" height="302" alt="BestChoice" style={{ objectFit: 'contain', width: '100%', height: '100%' }} /> */}
<div className="container-fluid d-flex flex-row align-items-center justify-content-center pt-5">

  <br></br><br></br>
  <div className="black-white">
    <h2 className='best-choice-tittle pt-4'> PROMOTIONS</h2>
  </div>
</div>

</div>



                  <div className='container-fluid car-container d-flex py-4 hide-fleet-on-mobile'>

                <div class="row">


                  <div className="row row-cols-1 row-cols-md-3 g-4">
                        {carCategoriesComponents}

                    </div>
                </div>

                </div>
            </div>

            <Footer/>
        </div>
    )
}

export default BrandDetailCarComponent;
