import React, { useEffect } from 'react';

function CarouselHomeEn(props) {
  useEffect(() => {
    const carousel = document.querySelector('.carousel');
    const carouselInstance = new window.bootstrap.Carousel(carousel, {
      interval: 3000,
      wrap: true,
    });
    return () => {
      carouselInstance.dispose();
    };
  }, []);

  return (
    <div id="carouselExampleInterval" className="carousel slide" data-bs-ride="carousel" data-bs-touch="true" data-bs-pause="false">
      <div className="carousel-inner">
        <div className="carousel-item active">
          <img fetchpriority="high" src={props.c1} className="d-flex w-100 h-100" style={{objectFit:"scale-up "}}  width= "200" alt="..."/>
        </div>
        <div className="carousel-item">
          <img fetchpriority="low" src={props.c2} className="d-flex w-100 h-100" style={{objectFit:"scale-up "}}  width= "200"  alt="..."/>
        </div>
        <div className="carousel-item">
          <img fetchpriority="low" src={props.c3} className="d-flex w-100 h-100" style={{objectFit:"scale-up "}}  width= "200"  alt="..."/>
        </div>
        <div className="carousel-item">
          <img fetchpriority="low" src={props.c4} className="d-flex w-100 h-100" style={{objectFit:"scale-up "}}  width= "200"  alt="..."/>
        </div>
      </div>
    </div>
  );
};

export default CarouselHomeEn;
